INSERT INTO `w_my_cart` (`id`, `product_id`, `ord_cat`, `ord_list`, `product_category`, `name`, `description`, `price`, `url`, `img`, `delivery`) VALUES
(12, 'WS - SA', 2, 1, 'Subscriptions', 'South Africa', 'Wildside Magazine, published quarterly, is a quality, exciting,  ecotravel, adventure and conservation read. Its primary editorial focus is KwaZulu-Natal, South Africa including the East African Seaboard with select options in Africa. \r\n\r\nWildside is read by people who love to travel, care about the environment, enjoy outdoor activities like snorkelling, fishing, bird watching, walking, horse riding, abseiling, or just relaxing at a mountain retreat or a quiet bush lodge. \r\n\r\nWildside seeks unique travel destinations, exciting adventure, and brings readers seriously great stuff to do for all ages, including families. There is always a significant amount of content in Wildside, and often more content than in other magazines in this genre.\r\n\r\nDelivery to South African post boxes is included in the subscription price.', '14.00', '', 'covergreen.jpg', '0.00'),
(13, 'WS - INTERNATIONAL', 2, 2, 'Subscriptions', 'International', 'Your Wildside Magazine delivered anywhere in the world.\r\n\r\nWildside Magazine, published quarterly, is a quality, exciting,  ecotravel, adventure and conservation read. Its primary editorial focus is KwaZulu-Natal, South Africa including the East African Seaboard with select options in Africa. \r\n\r\nWildside is read by people who love to travel, care about the environment, enjoy outdoor activities like snorkelling, fishing, bird watching, walking, horse riding, abseiling, or just relaxing at a mountain retreat or a quiet bush lodge. \r\n\r\nWildside seeks unique travel destinations, exciting adventure, and brings readers seriously great stuff to do for all ages, including families. There is always a significant amount of content in Wildside, and often more content than in other magazines in this genre.\r\n\r\nDelivery to anywhere in the World is included in the subscription price.', '39.00', '', 'coverlarge.jpg', '0.00'),
(16, 'WS - WARRIOR - FREE', 0, 1, 'Wildside Warrior Community', 'Join today for free and get immediate access to your online special rhino edition for the iPad', 'You have a FREE online mag of the Wildside - Special Rhino edition waiting for you after you have registered.\r\n\r\nPlease click on the button below to access your Free iPad edition.\r\n\r\n<a href="http://www.wildsidesa.co.za/ipad/">Click here for your free iPad rhino edition</a>', '0.00', '', 'ipad-cover-for-sale.jpg', '0.00'),
(17, 'WS - WARRIOR - SA', 1, 1, 'Wildside Warrior', 'South Africa', 'WILDSIDE WARRIOR – South African \r\n(INCLUDING WILDSIDE SUBSCRIPTION) \r\n\r\nYou will receive: \r\nAn annual subscription to Wildside - four editions per year\r\nReceive information about special accommodation discounts\r\nYour Rhino Warrior vehicle sticker\r\nBe entered into all Wildside competitions for one year\r\nYour name listed on the Wildside Warrior website list (optional) for your donation', '37.00', '', 'wwmember_subs_sa.jpg', '0.00'),
(18, 'WS - WARRIOR - INT', 1, 2, 'Wildside Warrior', 'International', 'WILDSIDE WARRIOR – International \r\n(INCLUDING WILDSIDE SUBSCRIPTION) \r\n\r\nYou will receive: \r\nAn annual subscription to Wildside - four editions per year\r\nReceive information about special accommodation discounts\r\nYour Rhino Warrior vehicle sticker\r\nBe entered into all Wildside competitions for one year\r\nYour name listed on the Wildside Warrior website list (optional) for your donation', '62.00', 'NULL', 'wwmember_subs_international.jpg', '0.00'),
(19, 'WS - WARRIOR - FREE', 0, 0, 'Wildside Warrior Community', 'BECOME A  WILDSIDE WARRIOR FOR RHINOS TODAY AND HELP STOP THE POACHING', 'Wildside Warriors is for people who want to contribute to anti-poaching efforts, who enjoy nature and travelling and will benefit from accommodation discounts. \r\n\r\nWildside will <b>donate R50 to Project Rhino KZN</b> for every Wildside Warrior. Project Rhino KZN is administered by the African Conservation Trust and solely for anti-poaching equipment and efforts.\r\n\r\n<a href="http://www.wildsidesa.co.za/shop/products.php?id=Wildside%20Warrior">I would like to become a Wildside Warrior with a donation</a> ', '0.00', '', '0', '0.00'),
(20, '', 0, 0, '', '', '', '0.00', '', '', '0.00');
